import React, {useState} from 'react';

import { booking } from '../../../settings';
import { StyledBookingInput, StyledBookingForm, StyledButton } from '../BookingEndpoint/Booking.styles';
import { MyBody } from '../BookingEndpoint/Booking.styles';

const BookingEndpoint = props => {
    const [booking, setbooking] = useState(props.booking)

const handleSubmit = e =>{
    e.preventDefault();
    fetch(booking, requestOptions)
    .then(response => response.json())
    .then(json => setbooking(json.booking))
}

const requestOptions = {
    method: 'POST',
    body: JSON.stringify({ booking }),
    headers: { 'Content-Type': 'application/json'}
}



return (
    <MyBody>
        <StyledBookingForm id="dateduration">
            <StyledBookingInput 
            type="date"
            placeholder="Day/Month/Year"
            id="bookingDate">
            </StyledBookingInput>
            
            <StyledBookingInput
                type="number"
                placeholder="duration in minutes"
                id="duration"
            ></StyledBookingInput>
        

            <StyledButton type="submit" onSubmit={(handleSubmit)}>
            Confirm      
            </StyledButton>
        </StyledBookingForm>
    </MyBody>

    );
}
export default BookingEndpoint;
