import React, {useState} from 'react';
import axios from 'axios';


//Url
import { bookingUrl } from '../../../settings';
import { StyledBookingInput, StyledBookingForm, StyledButton, MyBody } from '../BookingEndpoint/Booking.styles';

const initialState = {
    bookingDate: "",
    duration: ""
}

function BookingEndpoint() {
    const [response, setResponse] = useState('');
    const [booking, setbooking] = useState({});


    const handleChange = (e) => {
        setbooking({
            ...booking,
            [e.target.id]: e.target.value,
        });
    };

const handleSubmit = (e) =>{
    e.preventDefault();
    axios.post(`${bookingUrl}`, {
        method: 'POST',
        data: booking,
    }).then((response) => {
        setResponse(response.data);
    }      
    
)};

return (
    <MyBody>
        <StyledBookingForm>
            <StyledBookingInput 
            type="date"
            id='bookingDate'
            value={booking.bookingDate}
            onChange={handleChange}
            ></StyledBookingInput>
            
            <StyledBookingInput
                type="number"
                placeholder="duration in minutes"
                id='duration'
                value={booking.duration}
                onChange={handleChange}
            ></StyledBookingInput>
            <StyledButton type="submit" onSubmit={(handleSubmit)}>
            Confirm      
            </StyledButton>
        </StyledBookingForm>
    </MyBody>

    );
}

export default BookingEndpoint;
