import React, {useState} from 'react';

//Url
import { bookingUrl } from '../../../settings';
import { StyledBookingInput, StyledBookingForm, StyledButton } from '../BookingEndpoint/Booking.styles';
import { MyBody } from '../BookingEndpoint/Booking.styles';

const BookingEndpoint = (props) => {
    const [booking, setbooking] = useState(props.booking)

const initialState = {
    bookingdate: "",
    duration: ""
};

const handleSubmit = e =>{
    e.preventDefault();
    fetch(bookingUrl, requestOptions)
    .then(response => response.json())
    .then(json => setbooking(json.booking))
}

const requestOptions = {
    method: 'POST',
    body: JSON.stringify({ booking }),
    headers: { 'Content-Type': 'application/json'}
}

const onChange = (e) => {
    setbooking({
        ...booking,
        [e.currentTarget.id]: e.currentTarget.value,
    });
};


return (
    <MyBody>
        <StyledBookingForm>
            <StyledBookingInput 
            type="date"
            placeholder="date"
            id='bookingdate'
            value={booking.bookingdate}
            onChange={onChange}
            ></StyledBookingInput>
            
            <StyledBookingInput
                type="number"
                placeholder="duration in minutes"
                id='duration'
                value={booking.duration}
                onChange={onChange}
            ></StyledBookingInput>

            <StyledButton type="submit" onSubmit={(handleSubmit)}>
            Confirm      
            </StyledButton>
        </StyledBookingForm>
    </MyBody>

    );
}
export default BookingEndpoint;
